// Advent of Code 2016, Day 2, Part One

#include <stdexcept>

constexpr const char* input[] = {
  "",
  "",
  "",
  "",
  ""
};

class Keypad {
public:
  constexpr void tryMoveUp() { if (row > 0) --row; }
  constexpr void tryMoveDown() { if (row < 2) ++row; }
  constexpr void tryMoveLeft() { if (col > 0) --col; }
  constexpr void tryMoveRight() { if (col < 2) ++col; }
  
  constexpr auto getKey() const { return row * 3 + col + 1; }
  
private:
  // zero-based
  int row = 1, col = 1;
};

constexpr auto stringLength(const char* str) {
  auto len = 0;
  
  while (str[0] != '\0') {
    ++len;
    ++str;
  }
  
  return len;
}

constexpr auto getKey(const char* str) {
  Keypad keypad;
  
  const auto len = stringLength(str);
  
  for (auto i = 0; i < len; ++i) {
    switch (str[i]) {
      case 'R':
        keypad.tryMoveRight();
        break;
      case 'D':
        keypad.tryMoveDown();
        break;
      case 'L':
        keypad.tryMoveLeft();
        break;
      case 'U':
        keypad.tryMoveUp();
        break;
      default:
        throw std::invalid_argument("");
    }
  }
  
  return keypad.getKey();
}

constexpr auto getCode() {
  auto code = 0;
  
  for (auto i = 0u; i < sizeof(input)/sizeof(char*); ++i) {
    code = code * 10 + getKey(input[i]);
  }
  
  return code;
}

int main() {
  constexpr auto code = getCode();
  return code;
}
